private void scdetMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_scdetMouseClicked
// TODO add your handling code here:
    
    
    
    String stext=searchtext.getText();
    connector ob=new connector();
            
     try
        {
           Connection my=ob.connect();
        
        
           Statement s=my.createStatement();
          
           ResultSet rs = null;
    if(byname.isSelected())
    {
       
           rs=s.executeQuery("select * from cinfo where cname='"+stext+"' order by cname");
          
    }
    else if(bypno.isSelected())
    {
        
          rs=s.executeQuery("select * from cinfo where cphone='"+stext+"' order by cname");
    }
    else if(bycaid.isSelected())
    {
        
           rs=s.executeQuery("select * from cinfo where cappid='"+stext+"' order by cname");
    }
    else if(bycid.isSelected())
    {
        
           rs=s.executeQuery("select * from cinfo where cid='"+stext+"' order by cname");
    }
     
     
    int cap=0;;
    while(rs.next())
    {
        String cinf=null;
        String cname,cid,cappid,cpn,caddr;
       
        
        cname="Cus Name: "+rs.getString("cname");
        cid="Cus ID: "+rs.getString("cid");
        cappid="Cus AppID: "+rs.getString("cappid");
        cpn="Cus PhoneNo: "+rs.getString("cphone");
        caddr="Cus Addr: "+rs.getString("caddr");
        cap=Integer.parseInt(rs.getString("cappid"));
        cinf=cname+"\n"+cid+"\n"+cappid+"\n"+cpn+"\n"+caddr+"\n\n";
        cdet.append(cinf);
        
        chooseappid.addItem(rs.getString("cappid"));
        
        cusid=rs.getInt("cid");
        o.setcid(cusid);
        System.out.println(cap);
        ResultSet rk=s.executeQuery("select * from comp where cappid="+cap); 
        
        while(rk.next())
        {
            cplist.addItem(rk.getString("cpid"));
        }
      
    }
        
     }
     catch(Exception e)
     {
         System.out.println("some error"+e);
     }
    
   
}//GEN-LAST:event_scdetMouseClicked

