private void spdetMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_spdetMouseClicked
// TODO add your handling code here:
    
        String scapid=(String)chooseappid.getSelectedItem();
        
        int capid=Integer.parseInt(scapid);
        o.setcapid(capid);
        
        connector ob=new connector();
            
     try
        {
           Connection my=ob.connect();
        
        
           Statement s=my.createStatement();
          
           ResultSet rs = s.executeQuery("select * from capp where cappid="+capid); 
       
           
           if(rs.next())
           {
           String pinf,dop,aid,mop,wp;
         
           dop="DOP: "+rs.getString("dop");  
           aid="App ID: "+rs.getInt("appid");   
           mop="Pay Mode: "+rs.getString("mop");   
           wp="Warranty: "+rs.getInt("wp");   
           
           pinf=aid+"\n"+dop+"\n"+mop+"\n"+wp+"\n\n";//product info
           
           pdet.append(pinf);
           
           
           }
               // System.out.println(capid);
           ResultSet rp = s.executeQuery("select cpid from comp where cappid="+capid+"");
      
           while(rp.next())
           {
               
                cplist.addItem(rp.getString("cpid"));
           }
     }
     catch(Exception e)
     {
         System.out.println("some error"+e);
     }
}//GEN-LAST:event_spdetMouseClicked

private void gcdetMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_gcdetMouseClicked
// TODO add your handling code here:
    
    String scpid=(String)cplist.getSelectedItem();
    int cpid=Integer.parseInt(scpid);
    System.out.println(cpid);
    o.setcpid(cpid);
            connector ob=new connector();
            
     try
        {
           Connection my=ob.connect();
        
        
           Statement s=my.createStatement();
          
           ResultSet rs = s.executeQuery("select * from comp where cpid="+cpid);
           
           String cpinf,cpdesc,cpdate,cprem;
           if(rs.next())
           {
           cpdesc="Desc: "+rs.getString("cpdesc");
           cpdate="Date :"+rs.getString("cpdate");
           cprem="Remarks: "+rs.getString("cpremark");
           
           cpinf=cpdesc+"\n"+cpdate+"\n"+cprem+"\n\n";
           
           cpdet.append(cpinf);
           }
     }
     catch(Exception e)
     {
         System.out.println(e);
     }
       

}//GEN-LAST:event_gcdetMouseClicked

